package mypackage;

public class TypeCastingDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c ='A';
		// TypeCasting
		int b =c;
		float d = b;
		double e = b;
		long f = b;
		System.out.println("b:"+b);
		System.out.println("d:"+d);
		System.out.println("e:"+e);
		System.out.println("f:"+f);
		
		

	}

}
